import { useState, useEffect } from "react";
import { supabase } from "../../supabaseClient";
import Button from "../ui/Button";
import Input from "../ui/Input";
import Label from "../ui/Label";
import Select from "../ui/Select";

export default function ProfileForm({ profile, onSaved }) {
  const [showMessage, setShowMessage] = useState(false);

  // Solo el estado para inputs que quieres controlar
  const [fullName, setFullName] = useState("");
  const [phone, setPhone] = useState("");
  const [programa, setPrograma] = useState("");

  // Inicializa los estados cuando profile llegue
  useEffect(() => {
    if (profile) {
      setFullName(profile.full_name || "");
      setPhone(profile.phone || "");
      setPrograma(profile.programa_educativo || "");
    }
  }, [profile]);

  const onSubmit = async (e) => {
    e.preventDefault();

    const { data, error } = await supabase
      .from("profiles")
      .update({
        full_name: fullName,
        phone,
        programa_educativo: programa,
      })
      .eq("id", profile.id)
      .select()
      .single();

    if (!error) {
      onSaved(data);
      setShowMessage(true);
      setTimeout(() => setShowMessage(false), 2000);
    }
  };

  return (
    <div>
      <h3 className="profile-title">Mi Perfil</h3>
      <form onSubmit={onSubmit} className="profile-form">
        <div>
          <Label>Nombre completo</Label>
          <Input
            name="full_name"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />
        </div>
        <div>
          <Label>Teléfono</Label>
          <Input
            name="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>
        <div>
          <Label>Programa educativo</Label>
          <Select
            name="programa"
            value={programa}           // ahora controlado por estado
            onChange={(e) => setPrograma(e.target.value)}
          >
            <option value="">Selecciona programa…</option>
            <option value="Administración">Administración</option>
            <option value="Contaduría">Contaduría</option>
            <option value="Proyectos">Proyectos</option>
          </Select>
        </div>
        <Button>Guardar</Button>
      </form>

      {showMessage && (
        <div className="modal-overlay">
          <div className="modal-content">Guardado con éxito</div>
        </div>
      )}
    </div>
  );
}
