import { useState, useEffect } from "react";
import { supabase } from "../../supabaseClient";
import Button from "../ui/Button";
import Input from "../ui/Input";
import Label from "../ui/Label";
import Select from "../ui/Select";

export default function ProfileForm({ profile, onSaved }) {
  const [showMessage, setShowMessage] = useState(false);

  // Estados para inputs
  const [fullName, setFullName] = useState("");
  const [phone, setPhone] = useState("");
  const [tipoPrograma, setTipoPrograma] = useState(""); // TSU o Licenciatura
  const [cuatrimestre, setCuatrimestre] = useState(""); // 1, 4, 7, 10
  const [programa, setPrograma] = useState(""); // P.E. Administración o Contaduría

  // Inicializa los estados cuando profile llegue
  useEffect(() => {
    if (profile) {
      setFullName(profile.full_name || "");
      setPhone(profile.phone || "");
      setTipoPrograma(profile.tipo_programa || "");
      setCuatrimestre(profile.cuatrimestre || "");
      setPrograma(profile.programa_educativo || "");
    }
  }, [profile]);

  const onSubmit = async (e) => {
    e.preventDefault();

    const { data, error } = await supabase
      .from("profiles")
      .update({
        full_name: fullName,
        phone,
        tipo_programa: tipoPrograma,
        cuatrimestre,
        programa_educativo: programa,
      })
      .eq("id", profile.id)
      .select()
      .single();

    if (!error) {
      onSaved(data);
      setShowMessage(true);
      setTimeout(() => setShowMessage(false), 2000);
    }
  };

  return (
    <div>
      <h3 className="profile-title">Mi Perfil</h3>
      <form onSubmit={onSubmit} className="profile-form">
        <div>
          <Label>Nombre completo</Label>
          <Input
            name="full_name"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />
        </div>

        <div>
          <Label>Teléfono</Label>
          <Input
            name="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>

        <div>
          <Label>Tipo de programa</Label>
          <Select
            name="tipo_programa"
            value={tipoPrograma}
            onChange={(e) => setTipoPrograma(e.target.value)}
          >
            <option value="">Selecciona tipo…</option>
            <option value="TSU">TSU</option>
            <option value="Licenciatura">Licenciatura</option>
          </Select>
        </div>

        <div>
          <Label>Cuatrimestre</Label>
          <Select
            name="cuatrimestre"
            value={cuatrimestre}
            onChange={(e) => setCuatrimestre(e.target.value)}
          >
            <option value="">Selecciona cuatrimestre…</option>
            <option value="1">1°</option>
            <option value="4">4°</option>
            <option value="7">7°</option>
            <option value="10">10°</option>
          </Select>
        </div>

        <div>
          <Label>Programa educativo</Label>
          <Select
            name="programa"
            value={programa}
            onChange={(e) => setPrograma(e.target.value)}
          >
            <option value="">Selecciona programa…</option>
            <option value="Administración">Administración</option>
            <option value="Contaduría">Contaduría</option>
          </Select>
        </div>

        <Button>Guardar</Button>
      </form>

      {showMessage && (
        <div className="modal-overlay">
          <div className="modal-content">Guardado con éxito</div>
        </div>
      )}
    </div>
  );
}
