import { useEffect, useState } from "react";
import dayjs from "dayjs";
import { supabase } from "../../supabaseClient";
import EventForm from "./EventForm";
import EventActions from "./EventActions";

export default function AdminEventos() {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingEvent, setEditingEvent] = useState(null); // evento en edición

  useEffect(() => {
    fetchData();
  }, []);

  async function fetchData() {  
    setLoading(true);
    const { data } = await supabase.rpc("events_with_counts");
    setLoading(false);
    setEvents(data || []);
  }

  return (
    <div style={{ display: "grid", gap: 12, gridTemplateColumns: "1fr 1fr" }}>
      {/* Formulario: modo crear si editingEvent es null, modo editar si hay evento */}
      <EventForm
        eventToEdit={editingEvent}
        onCreated={() => {
          fetchData();
          setEditingEvent(null); // 🔹 limpia para volver a modo Crear
        }}
        onCancel={() => setEditingEvent(null)} // 🔹 cancelar edición
      />

      <div>
        <h4 style={{ fontWeight: 700, marginBottom: 8 }}>Eventos creados</h4>
        {loading ? (
          <p>Cargando...</p>
        ) : (
          <div style={{ display: "grid", gap: 8 }}>
            {events.map((e) => (
              <div
                key={e.id}
                style={{
                  border: "1px solid #e5e7eb",
                  borderRadius: 16,
                  padding: 12,
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}
                >
                  <div>
                    <p style={{ fontWeight: 700 }}>
                      {e.title}{" "}
                      <span style={{ fontSize: 12, color: "#6b7280" }}>
                        ({e.type})
                      </span>
                    </p>
                    <p style={{ fontSize: 12, color: "#6b7280" }}>
                      {dayjs(e.starts_at).format("DD/MM/YYYY HH:mm")}
                    </p>
                    <p style={{ fontSize: 12 }}>
                      Cupo: {e.registered_count}/{e.capacity}
                    </p>
                  </div>
                  {/* Botones de acciones, incluyendo Editar */}
                  <EventActions
                    eventId={e.id}
                    onChanged={fetchData}
                    onEdit={() => setEditingEvent(e)} // pasa el evento al formulario
                  />
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
