import { useState, useEffect } from "react";
import { supabase } from "../../supabaseClient";
import Button from "../ui/Button";
import Input from "../ui/Input";
import Label from "../ui/Label";
import Select from "../ui/Select";

export default function EventForm({ onCreated, eventToEdit, onCancel }) {
  const [title, setTitle] = useState("");
  const [type, setType] = useState("taller");
  const [starts_at, setStartsAt] = useState(""); // fecha/hora de inicio (se mantiene)
  const [ends_at, setEndsAt] = useState(""); // ⏰ ahora solo hora final
  const [capacity, setCapacity] = useState(50);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    if (eventToEdit) {
      setTitle(eventToEdit.title || "");
      setType(eventToEdit.type || "taller");

      // Mantener inicio como antes (fecha + hora)
      if (eventToEdit.starts_at) {
        const dt = new Date(eventToEdit.starts_at);
        const local = new Date(dt.getTime() - dt.getTimezoneOffset() * 60000);
        setStartsAt(local.toISOString().slice(0, 16));
      } else {
        setStartsAt("");
      }

      // ⚡ Guardamos solo la hora final (HH:mm)
      if (eventToEdit.ends_at) {
        const h = new Date(eventToEdit.ends_at).toLocaleTimeString("es-MX", {
          hour: "2-digit",
          minute: "2-digit",
          hour12: false,
        });
        setEndsAt(h);
      } else {
        setEndsAt("");
      }

      setCapacity(eventToEdit.capacity || 50);
    } else {
      resetForm();
    }
  }, [eventToEdit]);

  const resetForm = () => {
    setTitle("");
    setType("taller");
    setStartsAt("");
    setEndsAt("");
    setCapacity(50);
    setErrorMessage("");
  };

  async function submit(e) {
    e.preventDefault();
    setLoading(true);
    setErrorMessage("");

    // Validación: hora final debe ser mayor que inicio (si ambos existen)
    if (starts_at && ends_at) {
      const startDate = new Date(starts_at);
      const [h, m] = ends_at.split(":");
      const endDate = new Date(startDate);
      endDate.setHours(parseInt(h), parseInt(m), 0, 0);

      if (endDate <= startDate) {
        setErrorMessage("La hora de fin debe ser posterior a la de inicio.");
        setLoading(false);
        return;
      }
    }

    const {
      data: { user },
    } = await supabase.auth.getUser();

    try {
      if (eventToEdit) {
        const { error } = await supabase
          .from("events")
          .update({
            title,
            type,
            starts_at: starts_at || null,
            ends_at: ends_at || null, // ⚡ ahora solo HH:mm
            capacity,
          })
          .eq("id", eventToEdit.id);
        if (error) throw error;
      } else {
        const { error } = await supabase.from("events").insert({
          title,
          type,
          starts_at: starts_at || null,
          ends_at: ends_at || null, // ⚡ solo HH:mm
          capacity,
          created_by: user.id,
        });
        if (error) throw error;
      }

      resetForm();
      onCreated?.();
    } catch (error) {
      setErrorMessage("Error al guardar el evento: " + error.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div>
      <h4 style={{ fontWeight: 700, marginBottom: 8 }}>
        {eventToEdit ? "Editar evento" : "Crear evento"}
      </h4>
      <form onSubmit={submit} style={{ display: "grid", gap: 8 }}>
        <div>
          <Label>Título</Label>
          <textarea
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={{
              width: "100%",
              minHeight: "60px",
              padding: "8px",
              fontSize: "14px",
              borderRadius: "4px",
              border: "1px solid #ccc",
              resize: "vertical",
            }}
          />
        </div>
        <div>
          <Label>Tipo</Label>
          <Select value={type} onChange={(e) => setType(e.target.value)}>
            <option value="taller">Taller</option>
            <option value="conferencia">Conferencia</option>
            <option value="concurso">Concurso</option>
          </Select>
        </div>
        <div>
          <Label>Fecha y hora de inicio</Label>
          <Input
            type="datetime-local"
            value={starts_at}
            onChange={(e) => setStartsAt(e.target.value)}
            required
          />
        </div>
        <div>
          <Label>Hora de fin</Label>
          <Input
            type="time"
            value={ends_at}
            onChange={(e) => setEndsAt(e.target.value)}
          />
          {errorMessage && (
            <p style={{ color: "red", fontSize: "12px", marginTop: "4px" }}>
              {errorMessage}
            </p>
          )}
        </div>
        <div>
          <Label>Cupo máximo</Label>
          <Input
            type="number"
            min={1}
            value={capacity}
            onChange={(e) => setCapacity(parseInt(e.target.value || "0"))}
            required
          />
        </div>
        <div style={{ display: "flex", gap: 8 }}>
          <Button type="submit" disabled={loading}>
            {loading ? "Guardando..." : eventToEdit ? "Actualizar" : "Crear"}
          </Button>
          {onCancel && (
            <Button
              type="button"
              onClick={() => {
                resetForm();
                onCancel();
              }}
              style={{ backgroundColor: "#ccc", color: "#000" }}
            >
              Cancelar
            </Button>
          )}
        </div>
      </form>
    </div>
  );
}
