import { useEffect, useState, useRef } from "react";
import dayjs from "dayjs";
import { supabase } from "../../supabaseClient";
import EventForm from "./EventForm";
import EventActions from "./EventActions";

export default function AdminEventos() {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingEvent, setEditingEvent] = useState(null);
  const formRef = useRef(null); // ðŸ”¹ referencia al formulario

  useEffect(() => {
    fetchData();
  }, []);

  async function fetchData() {
    setLoading(true);
    const { data } = await supabase.rpc("events_with_counts");
    setLoading(false);
    setEvents(data || []);
  }

  const handleEdit = (event) => {
    setEditingEvent(event); 
    // ðŸ”¹ desplaza la pantalla al formulario
    if (formRef.current) {
      formRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  };

  return (
    <div style={{ display: "grid", gap: 20 }}>
      {/* ðŸ”¹ Formulario arriba */}
      <div ref={formRef}>
        <EventForm
          eventToEdit={editingEvent}
          onCreated={() => {
            fetchData();
            setEditingEvent(null);
          }}
          onCancel={() => setEditingEvent(null)}
        />
      </div>

      {/* ðŸ”¹ Eventos abajo en 2 columnas */}
      <div>
        <h4 style={{ fontWeight: 700, marginBottom: 8 }}>Eventos creados</h4>
        {loading ? (
          <p>Cargando...</p>
        ) : (
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "1fr 1fr",
              gap: 12,
            }}
          >
            {events.map((e) => (
              <div
                key={e.id}
                style={{
                  border: "1px solid #e5e7eb",
                  borderRadius: 16,
                  padding: 12,
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}
                >
                  <div>
                    <p style={{ fontWeight: 700 }}>
                      {e.title}{" "}
                      <span style={{ fontSize: 12, color: "#6b7280" }}>
                        ({e.type})
                      </span>
                    </p>
                    <p style={{ fontSize: 12, color: "#6b7280" }}>
                      {dayjs(e.starts_at).format("DD/MM/YYYY HH:mm")}
                    </p>
                    <p style={{ fontSize: 12 }}>
                      Cupo: {e.registered_count}/{e.capacity}
                    </p>
                  </div>
                  <EventActions
                    eventId={e.id}
                    onChanged={fetchData}
                    onEdit={() => handleEdit(e)} // âœ… ahora tambiÃ©n hace scroll
                  />
                </div>
              </div>
            ))}
            {events.length === 0 && (
              <p style={{ gridColumn: "1 / -1", color: "#6b7280" }}>
                No hay eventos creados aÃºn.
              </p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
