import { useState } from "react";
import { supabase } from "../../supabaseClient";
import Button from "../ui/Button";
import Input from "../ui/Input";
import Label from "../ui/Label";
import Select from "../ui/Select";

export default function ProfileForm({ profile, onSaved }) {
  const [showMessage, setShowMessage] = useState(false);

  const onSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData(e.target);
    const full_name = formData.get("full_name");
    const phone = formData.get("phone");
    const programa = formData.get("programa");

    const { data, error } = await supabase
      .from("profiles")
      .update({
        full_name,
        phone,
        programa_educativo: programa,
      })
      .eq("id", profile.id)
      .select()
      .single();

    if (!error) {
      onSaved(data);
      setShowMessage(true);
      setTimeout(() => setShowMessage(false), 2000);
    }
  };

  return (
    <div>
      <h3 className="profile-title">Mi Perfil</h3>
      <form onSubmit={onSubmit} className="profile-form">
        <div>
          <Label>Nombre completo</Label>
          <Input
            name="full_name"
            defaultValue={profile?.full_name ?? ""}
          />
        </div>
        <div>
          <Label>Teléfono</Label>
          <Input
            name="phone"
            defaultValue={profile?.phone ?? ""}
          />
        </div>
        <div>
          <Label>Programa educativo</Label>
          <Select
            name="programa"
            defaultValue={profile?.programa_educativo ?? ""}
          >
            <option value="">Selecciona programa…</option>
            <option value="Administración">Administración</option>
            <option value="Contaduría">Contaduría</option>
            <option value="Proyectos">Proyectos</option>
          </Select>
        </div>
        <Button>Guardar</Button>
      </form>

      {showMessage && (
        <div className="modal-overlay">
          <div className="modal-content">Guardado con éxito</div>
        </div>
      )}
    </div>
  );
}
