import { useEffect, useState } from "react";
import { supabase } from "../../supabaseClient";
import Button from "../ui/Button";
import Input from "../ui/Input";
import Label from "../ui/Label";
import Select from "../ui/Select";

export default function ProfileForm({ profile, onSaved }) {
  const [full_name, setFullName] = useState(profile?.full_name || "");
  const [phone, setPhone] = useState(profile?.phone || "");
  const [programa, setPrograma] = useState(profile?.programa_educativo || "");
  const [showMessage, setShowMessage] = useState(false);

  // ✅ Ahora depende de todo el objeto profile
  useEffect(() => {
    if (profile) {
      setFullName(profile.full_name || "");
      setPhone(profile.phone || "");
      setPrograma(profile.programa_educativo || "");
    }
  }, [profile]);

  const onSubmit = async (e) => {
    e.preventDefault();
    const { data, error } = await supabase
      .from("profiles")
      .update({ full_name, phone, programa_educativo: programa })
      .eq("id", profile.id)
      .select()
      .single();

    if (!error) {
      onSaved(data);
      setShowMessage(true);
      setTimeout(() => setShowMessage(false), 3000);
    }
  };

  return (
    <div>
      <h3 className="profile-title">Mi Perfil</h3>
      <form onSubmit={onSubmit} className="profile-form">
        <div>
          <Label>Nombre completo</Label>
          <Input value={full_name} onChange={(e) => setFullName(e.target.value)} />
        </div>
        <div>
          <Label>Teléfono</Label>
          <Input value={phone} onChange={(e) => setPhone(e.target.value)} />
        </div>
        <div>
          <Label>Programa educativo</Label>
          <Select value={programa} onChange={(e) => setPrograma(e.target.value)}>
            <option value="">Selecciona programa…</option>
            <option value="Administración">Administración</option>
            <option value="Contaduría">Contaduría</option>
            <option value="Proyectos">Proyectos</option>
          </Select>
        </div>
        <Button>Guardar</Button>
      </form>

      {showMessage && (
        <div className="modal-overlay">
          <div className="modal-content">Guardado con éxito</div>
        </div>
      )}
    </div>
  );
}
