import { useEffect, useState } from "react";
import { supabase } from "../../supabaseClient";
import Button from "../ui/Button";
import Input from "../ui/Input";
import Label from "../ui/Label";
import Select from "../ui/Select";

export default function ProfileForm({ profile, onSaved }) {
  const [full_name, setFullName] = useState("");
  const [phone, setPhone] = useState("");
  const [programa, setPrograma] = useState("");
  const [showMessage, setShowMessage] = useState(false);

  // ✅ sincroniza SIEMPRE que llegue un perfil nuevo
  useEffect(() => {
    if (profile?.id) {
      setFullName(profile.full_name ?? "");
      setPhone(profile.phone ?? "");
      setPrograma(profile.programa_educativo ?? "");
    }
  }, [profile]); // <- escucha el objeto completo, no solo el id

  const onSubmit = async (e) => {
    e.preventDefault();
    const { data, error } = await supabase
      .from("profiles")
      .update({
        full_name,
        phone,
        programa_educativo: programa,
      })
      .eq("id", profile.id)
      .select()
      .single();

    if (!error) {
      onSaved(data); // <- actualiza el padre
      setShowMessage(true);
      setTimeout(() => setShowMessage(false), 3000);
    }
  };

  return (
    <div>
      <h3 className="profile-title">Mi Perfil</h3>
      <form onSubmit={onSubmit} className="profile-form">
        <div>
          <Label>Nombre completo</Label>
          <Input
            value={full_name}
            onChange={(e) => setFullName(e.target.value)}
          />
        </div>
        <div>
          <Label>Teléfono</Label>
          <Input value={phone} onChange={(e) => setPhone(e.target.value)} />
        </div>
        <div>
          <Label>Programa educativo</Label>
          <Select
            value={programa} // <- mantiene la selección
            onChange={(e) => setPrograma(e.target.value)}
          >
            <option value="">Selecciona programa…</option>
            <option value="Administración">Administración</option>
            <option value="Contaduría">Contaduría</option>
            <option value="Proyectos">Proyectos</option>
          </Select>
        </div>
        <Button>Guardar</Button>
      </form>

      {showMessage && (
        <div className="modal-overlay">
          <div className="modal-content">Guardado con éxito</div>
        </div>
      )}
    </div>
  );
}
