import { useState, useEffect } from "react";
import { supabase } from "../../supabaseClient";
import Button from "../ui/Button";
import Input from "../ui/Input";
import Label from "../ui/Label";
import Select from "../ui/Select";

export default function EventForm({ onCreated, eventToEdit, onCancel }) {
  const [title, setTitle] = useState("");
  const [type, setType] = useState("taller");
  const [starts_at, setStartsAt] = useState("");
  const [capacity, setCapacity] = useState(50);
  const [loading, setLoading] = useState(false);

  // Inicializamos el formulario si hay un evento a editar
  useEffect(() => {
    if (eventToEdit) {
      setTitle(eventToEdit.title || "");
      setType(eventToEdit.type || "taller");

      // Guardamos tal cual el valor para datetime-local
      if (eventToEdit.starts_at) {
        const dt = new Date(eventToEdit.starts_at);
        // Ajuste para que no se desplace por zona horaria
        const local = new Date(dt.getTime() - dt.getTimezoneOffset() * 60000);
        setStartsAt(local.toISOString().slice(0, 16));
      } else {
        setStartsAt("");
      }

      setCapacity(eventToEdit.capacity || 50);
    } else {
      resetForm();
    }
  }, [eventToEdit]);

  const resetForm = () => {
    setTitle("");
    setType("taller");
    setStartsAt("");
    setCapacity(50);
  };

  async function submit(e) {
    e.preventDefault();
    setLoading(true);
    const { data: { user } } = await supabase.auth.getUser();

    try {
      if (eventToEdit) {
        // Modo editar
        const { error } = await supabase
          .from("events")
          .update({
            title,
            type,
            // Guardamos directamente la cadena local (YYYY-MM-DDTHH:mm)
            starts_at: starts_at || null,
            capacity
          })
          .eq("id", eventToEdit.id);
        if (error) throw error;
      } else {
        // Modo crear
        const { error } = await supabase
          .from("events")
          .insert({
            title,
            type,
            starts_at: starts_at || null,
            capacity,
            created_by: user.id
          });
        if (error) throw error;
      }

      resetForm();
      onCreated?.(); // callback para refrescar la lista
    } catch (error) {
      alert("Error al guardar el evento: " + error.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div>
      <h4 style={{ fontWeight: 700, marginBottom: 8 }}>
        {eventToEdit ? "Editar evento" : "Crear evento"}
      </h4>
      <form onSubmit={submit} style={{ display: "grid", gap: 8 }}>
        <div>
          <Label>Título</Label>
          <textarea
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={{
              width: "100%",
              minHeight: "60px",
              padding: "8px",
              fontSize: "14px",
              borderRadius: "4px",
              border: "1px solid #ccc",
              resize: "vertical"
            }}
          />
        </div>
        <div>
          <Label>Tipo</Label>
          <Select value={type} onChange={(e) => setType(e.target.value)}>
            <option value="taller">Taller</option>
            <option value="conferencia">Conferencia</option>
            <option value="concurso">Concurso</option>
          </Select>
        </div>
        <div>
          <Label>Fecha y hora</Label>
          <Input
            type="datetime-local"
            value={starts_at}
            onChange={(e) => setStartsAt(e.target.value)}
            required
          />
        </div>
        <div>
          <Label>Cupo máximo</Label>
          <Input
            type="number"
            min={1}
            value={capacity}
            onChange={(e) => setCapacity(parseInt(e.target.value || "0"))}
            required
          />
        </div>
        <div style={{ display: "flex", gap: 8 }}>
          <Button type="submit" disabled={loading}>
            {loading ? "Guardando..." : eventToEdit ? "Actualizar" : "Crear"}
          </Button>
          {onCancel && (
            <Button
              type="button"
              onClick={() => {
                resetForm();
                onCancel();
              }}
              style={{ backgroundColor: "#ccc", color: "#000" }}
            >
              Cancelar
            </Button>
          )}
        </div>
      </form>
    </div>
  );
}
