import { useEffect, useRef } from "react";
import QRCode from "qrcode";
import dayjs from "dayjs";
import Badge from "../ui/Badge";

export default function QRItem({ reg }) {
  const canvasRef = useRef(null);

  useEffect(() => {
    // 游녤 El payload es lo que se leer치 con el esc치ner QR
    const payload = JSON.stringify({reg_id: reg.id,event_id: reg.event_id,});
    QRCode.toCanvas(canvasRef.current, payload, { width: 220 });

    {/* Cambiar para lector otro qr
    const payload = reg.id; // 游녣 solo el UUID de la inscripci칩n
    QRCode.toCanvas(canvasRef.current, payload, { width: 220 });
    */}

  }, [reg.id, reg.event_id]);

  return (
    <div
      style={{
        border: "1px solid #e5e7eb",
        borderRadius: 16,
        padding: 12,
        background: "#fff",
      }}
    >
      {/* Encabezado */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: 8,
        }}
      >
        <div>
          <p style={{ fontWeight: 700, fontSize: 14 }}>
            {reg.title}{" "}
            <span style={{ fontSize: 12, color: "#6b7280" }}>({reg.type})</span>
          </p>
          <p style={{ fontSize: 12, color: "#6b7280" }}>
            {dayjs(reg.starts_at).format("DD/MM/YYYY HH:mm")}
          </p>
        </div>
        <Badge>QR</Badge>
      </div>

      {/* QR */}
      <div style={{ display: "flex", justifyContent: "center" }}>
        <canvas ref={canvasRef} />
      </div>
    </div>
  );
}
