import { useEffect, useState } from "react";
import { supabase } from "../../supabaseClient";
import dayjs from "dayjs";
import QRItem from "./QRItem";

export default function MisRegistros({ refreshFlag }) {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  async function fetchRegistros() {
    const { data: { user } } = await supabase.auth.getUser();
    const { data, error } = await supabase
      .from("registrations_view")
      .select("id,event_id,title,type,starts_at,ends_at") // ðŸ”¹ agregamos ends_at
      .eq("user_id", user.id)
      .order("starts_at", { ascending: true });

    setLoading(false);
    if (!error) setItems(data || []);
  }

  useEffect(() => { fetchRegistros(); }, [refreshFlag]);

  return (
    <div>
      <h3 style={{ fontSize:16, fontWeight:700, marginBottom:8 }}>Mis registros / QR</h3>
      {loading ? <p>Cargando...</p> : (
        <div style={{ display:"grid", gap:12 }}>
          {items.map(it => (
            <QRItem
              key={it.id}
              reg={it}
              startTime={dayjs(it.starts_at).format("HH:mm")}
              endTime={it.ends_at ? dayjs(it.ends_at).format("HH:mm") : null} // ðŸ”¹ formateo de hora fin
            />
          ))}
          {items.length === 0 && <p style={{ color:"#6b7280" }}>AÃºn no te has inscrito.</p>}
        </div>
      )}
    </div>
  );
}
