import { useState } from "react";
import { useNavigate } from "react-router-dom"; // üëà para redirigir
import { supabase } from "../../supabaseClient";
import Button from "../ui/Button";
import Input from "../ui/Input";
import Label from "../ui/Label";
import Select from "../ui/Select";

export default function AuthForm() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [mode, setMode] = useState("login"); // login | signup
  const [role, setRole] = useState("alumno");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setMessage("");

    try {
      if (mode === "signup") {
        // 1Ô∏è‚É£ Verificar si el correo ya existe
        const { data: existingUser } = await supabase
          .from("profiles")
          .select("id")
          .eq("email", email)
          .single();

        if (existingUser) {
          setError("‚ùå Este correo ya est√° registrado. Intenta iniciar sesi√≥n.");
          return;
        }

        // 2Ô∏è‚É£ Crear usuario en auth
        const { data, error } = await supabase.auth.signUp({ email, password });
        if (error) throw error;

        const user = data.user;
        if (!user) {
          setError("‚ùå No se pudo crear el usuario. Intenta de nuevo.");
          return;
        }

        // 3Ô∏è‚É£ Crear perfil
        await supabase.from("profiles").insert({
          id: user.id,
          email,
          role,
          full_name: "",
          phone: ""
        });

        // 4Ô∏è‚É£ Mensaje de √©xito
        setMessage(
          "‚úÖ Registro exitoso. Ingresa a tu correo y valida tu cuenta para poder ingresar."
        );
        setEmail("");
        setPassword("");
        setRole("alumno");
      } else {
        // üîë Login
        const { error } = await supabase.auth.signInWithPassword({ email, password });
        if (error) setError("‚ùå Credenciales incorrectas. Intenta de nuevo.");
      }
    } catch (err) {
      setError(err.message || "Ocurri√≥ un error inesperado.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={onSubmit} style={{ display: "grid", gap: 10 }}>
      {/* Botones modo */}
      <div style={{ display: "flex", gap: 8, fontSize: 14 }}>
        <Button type="button" onClick={() => setMode("login")}>
          Iniciar sesi√≥n
        </Button>
        <Button type="button" onClick={() => setMode("signup")}>
          Crear cuenta
        </Button>
      </div>

      {/* Selecci√≥n de rol en signup */}
      {mode === "signup" && (
        <div>
          <Label>Rol</Label>
          <Select value={role} onChange={(e) => setRole(e.target.value)}>
            <option value="alumno">Alumno</option>
           {/* <option value="admin">Admin (se validar√° en DB)</option>*/}
          </Select>
        </div>
      )}

      {/* Campos */}
      <div>
        <Label>Correo</Label>
        <Input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>

      <div>
        <Label>Contrase√±a</Label>
        <Input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
      </div>

      {/* Mensajes */}
      {error && <p style={{ color: "#dc2626", fontSize: 13 }}>{error}</p>}
      {message && <p style={{ color: "#16a34a", fontSize: 13 }}>{message}</p>}

      {/* Bot√≥n submit */}
      <Button disabled={loading}>
        {loading ? "Procesando..." : mode === "signup" ? "Registrarme" : "Entrar"}
      </Button>

      {/* Enlace a recuperar contrase√±a */}
      {mode === "login" && (
        <p style={{ fontSize: 14, textAlign: "center", marginTop: 8 }}>
          <button
            type="button"
            onClick={() => navigate("/forgot-password")}
            style={{
              background: "none",
              border: "none",
              color: "#2563eb",
              cursor: "pointer",
              textDecoration: "underline"
            }}
          >
            ¬øOlvidaste tu contrase√±a?
          </button>
        </p>
      )}
      {/* Registro */}
      {mode === "signup" && (
        <p>
          Una vez dando clic en <b>Registrarme</b>, revisa tu bandeja de correo para validar tu cuenta.
        </p>
      )}
     
    </form>
  );
}
