import { useState } from "react";
import { supabase } from "../../supabaseClient";



export default function ForgotPasswordForm() {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    const { error } = await supabase.auth.resetPasswordForEmail(email, {
      redirectTo: "http://localhost:3000/reset-password", // üëà ajusta si tu frontend tiene otra URL
    });

    if (error) {
      setMessage("‚ùå Error: " + error.message);
    } else {
      setMessage("üìß Revisa tu correo para restablecer la contrase√±a.");
    }
  };

  return (
    <form onSubmit={handleForgotPassword} style={{ display: "grid", gap: 12 }}>
      <h3>¬øOlvidaste tu contrase√±a?</h3>
      <input
        type="email"
        placeholder="Tu correo"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
        style={{ padding: 8, border: "1px solid #ccc", borderRadius: 8 }}
      />
      <button type="submit" style={{ padding: 8, borderRadius: 8, background: "#2563eb", color: "#fff" }}>
        Enviar enlace de recuperaci√≥n
      </button>
      {message && <p>{message}</p>}
    </form>
  );
}
