import { useState } from "react";
import { QrReader } from "react-qr-reader";
import { supabase } from "../../supabaseClient";

export default function QRScanner() {
  const [scanned, setScanned] = useState(null);
  const [message, setMessage] = useState("");

  // üëâ Detectar si es m√≥vil o desktop
  const isMobile = /Mobi|Android/i.test(navigator.userAgent);

  const handleResult = async (result) => {
    if (!!result) {
      const qrData = result?.text.trim();
      setScanned(qrData);

      try {
        // Actualizar check-in en Supabase
        const { data, error } = await supabase
          .from("registrations")
          .update({ checked_in: true, checked_at: new Date() })
          .eq("id", qrData)
          .select()
          .single();

        if (error) throw error;
        setMessage(`‚úÖ Check-in exitoso para ${data.nombre || "registro #" + data.id}`);
      } catch (err) {
        setMessage(`‚ùå Error: ${err.message}`);
      }
    }
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h2>Escanear QR</h2>
      <QrReader
        onResult={(result, error) => {
          if (!!result) handleResult(result);
          if (!!error) console.error(error);
        }}
        constraints={{
          facingMode: isMobile ? "environment" : "user", // üìå trasera en m√≥vil, webcam en PC
        }}
        style={{ width: "100%" }}
      />

      {scanned && <p>üì∑ C√≥digo detectado: {scanned}</p>}
      {message && <p>{message}</p>}
    </div>
  );
}
